<script>
import NavBar from '../components/Nav.vue';
import FooterPage from '../components/Footer.vue';
import axios from 'axios';

export default {
  name: 'MantenimientosPage',
  components: {
    NavBar,
    FooterPage,
  },
  data() {
    return {
      tabActual: 'canalesServicio', // Pestaña activa
      canalesServicio: [], // Lista de canales de servicio
      agencias: [], // Lista de agencias
      tiposCliente: [], // Lista de tipos de cliente
      tiposTransaccion: [], // Lista de tipos de transacción
      mostrarFormularioCanal: false, // Controla la visibilidad del formulario de creación de canal
      nuevoCanal: {
        codigoCanalServicio: '',
        nombreCanalServicio: '',
      },
      mostrarFormularioEditarCanal: false, // Controla la visibilidad del formulario de edición de canal
      canalEditado: {
        id: null,
        codigoCanalServicio: '',
        nombreCanalServicio: '',
      },
    };
  },
  async created() {
    await this.cargarDatos();
  },
  methods: {
    async cargarDatos() {
      const token = localStorage.getItem('token');
      try {
        // Cargar canales de servicio
        const responseCanales = await axios.get('http://127.0.0.1:8000/api/canales-servicio/', {
          headers: { Authorization: `Bearer ${token}` },
        });
        this.canalesServicio = responseCanales.data;

        // Cargar agencias
        const responseAgencias = await axios.get('http://127.0.0.1:8000/api/agencias/', {
          headers: { Authorization: `Bearer ${token}` },
        });
        this.agencias = responseAgencias.data;

        // Cargar tipos de cliente
        const responseTiposCliente = await axios.get('http://127.0.0.1:8000/api/tipos-cliente/', {
          headers: { Authorization: `Bearer ${token}` },
        });
        this.tiposCliente = responseTiposCliente.data;

        // Cargar tipos de transacción
        const responseTiposTransaccion = await axios.get('http://127.0.0.1:8000/api/tipos-transaccion/', {
          headers: { Authorization: `Bearer ${token}` },
        });
        this.tiposTransaccion = responseTiposTransaccion.data;
      } catch (error) {
        console.error('Error al cargar datos:', error);
      }
    },
    mostrarFormularioCrearCanal() {
      this.mostrarFormularioCanal = true;
    },
    async crearCanal() {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.post('http://127.0.0.1:8000/api/canales-servicio/', this.nuevoCanal, {
          headers: { Authorization: `Bearer ${token}` },
        });
        this.canalesServicio.push(response.data); // Agregar el nuevo canal a la lista
        this.mostrarFormularioCanal = false; // Cerrar el modal
        this.nuevoCanal = { codigoCanalServicio: '', nombreCanalServicio: '' }; // Limpiar el formulario
      } catch (error) {
        console.error('Error al crear el canal:', error);
      }
    },
    editarCanal(canal) {
      this.canalEditado = { ...canal }; // Copiar los datos del canal seleccionado
      this.mostrarFormularioEditarCanal = true; // Mostrar el modal
    },
    async guardarEdicionCanal() {
      try {
        const token = localStorage.getItem('token');
        await axios.put(`http://127.0.0.1:8000/api/canales-servicio/${this.canalEditado.id}/`, this.canalEditado, {
          headers: { Authorization: `Bearer ${token}` },
        });
        // Actualizar la lista de canales
        const index = this.canalesServicio.findIndex(c => c.id === this.canalEditado.id);
        this.canalesServicio.splice(index, 1, this.canalEditado);
        this.mostrarFormularioEditarCanal = false; // Cerrar el modal
      } catch (error) {
        console.error('Error al editar el canal:', error);
      }
    },
    async eliminarCanal(id) {
      if (confirm('¿Estás seguro de que deseas eliminar este canal?')) {
        try {
          const token = localStorage.getItem('token');
          await axios.delete(`http://127.0.0.1:8000/api/canales-servicio/${id}/`, {
            headers: { Authorization: `Bearer ${token}` },
          });
          // Eliminar el canal de la lista
          this.canalesServicio = this.canalesServicio.filter(c => c.id !== id);
        } catch (error) {
          console.error('Error al eliminar el canal:', error);
        }
      }
    },
    mostrarFormularioCrearAgencia() {
      // Lógica para mostrar formulario de creación de agencia
    },
    editarAgencia(agencia) {
      console.log(agencia);
    },
    eliminarAgencia(id) {
